// ðŸ”¶ Toggle the "See More / See Less" Image Section
function toggleMoreImages() {
  const moreImages = document.getElementById("moreImages");
  const toggleBtn = document.getElementById("toggleBtn");

  if (moreImages && toggleBtn) {
    if (moreImages.classList.contains("hidden")) {
      moreImages.classList.remove("hidden");
      toggleBtn.textContent = "See Less";
    } else {
      moreImages.classList.add("hidden");
      toggleBtn.textContent = "See More";
    }
  }
}

// ðŸ”¶ Lightbox Modal for Portfolio Images
function openModal(img) {
  const modal = document.getElementById("imageModal");
  const modalImg = document.getElementById("modalImage");

  if (modal && modalImg) {
    modal.style.display = "block";
    modalImg.src = img.src;
  }
}

// ðŸ”¶ Close Modal
function closeModal() {
  const modal = document.getElementById("imageModal");
  if (modal) {
    modal.style.display = "none";
  }
}

// ðŸ”¶ Close Modal on ESC Key
document.addEventListener("keydown", function (e) {
  if (e.key === "Escape") {
    closeModal();
  }
});
