// Lightbox Functions
function openLightbox(img) {
  images = Array.from(document.querySelectorAll(".gallery img"));
  currentIndex = images.indexOf(img);
  document.getElementById("lightboxModal").style.display = "flex";
  showImage();
  autoSlideInterval = setInterval(nextImage, 3000);
}

function closeLightbox() {
  document.getElementById("lightboxModal").style.display = "none";
  clearInterval(autoSlideInterval);
}

function showImage() {
  document.getElementById("lightboxImage").src = images[currentIndex].src;
}

function nextImage() {
  currentIndex = (currentIndex + 1) % images.length;
  showImage();
}

function prevImage() {
  currentIndex = (currentIndex - 1 + images.length) % images.length;
  showImage();
}

// Keyboard support
document.addEventListener("keydown", function (e) {
  const modalOpen = document.getElementById("lightboxModal").style.display === "flex";
  if (!modalOpen) return;

  if (e.key === "Escape") closeLightbox();
  if (e.key === "ArrowRight") {
    nextImage();
    resetAutoSlide();
  }
  if (e.key === "ArrowLeft") {
    prevImage();
    resetAutoSlide();
  }
});

function resetAutoSlide() {
  clearInterval(autoSlideInterval);
  autoSlideInterval = setInterval(nextImage, 3000);
}

// Toggle gallery visibility
function toggleGallery() {
  const gallery = document.getElementById("gallery");
  const button = document.getElementById("toggleBtn");

  gallery.classList.toggle("show-all");
  button.textContent = gallery.classList.contains("show-all") ? "Show Less" : "View All";
}
